import * as actions from '../actions/general.act'
const axios = require('axios');

export default store => next => action => {
    if (action.type == 'API') {
        const SERVER_ENDPOINT = "http://localhost:3000/moviesList";
        let { type } = action.payload;

        // store.dispatch(actions.api(type))
        axios.get(SERVER_ENDPOINT).then((res)=>console.log('xxxxx', res))
        // fetch({ url: SERVER_ENDPOINT, method: 'GET' }).then((res) => {
        //     let data = res.json()
        //     data.then((r) => console.log('xxxxx', r))
        // })
        //     .catch((err) => console.log(err))
    }
    return next(action)
}
