                }
        
                return id;
            }
        
            function _isIdExist(datas,id) {
                let moviesList = this.props.general.moviesList;
        
                for (var i = 0; i < datas.length; ++i) {
                    let movie = datas[i];
        
                    if (movie.id == id) {
                        return true;
                    }
                }
        
                return false;
            }
        }

        if (type == 'ADD') {
            let title = newData.title.replace(' ', '+');

            axios.get(OMDb_API + '&t=' + title + '&y=' + newData.year).then((respone) => {
