import React, { Component } from 'react';
// import './App.css';

class Buttons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            year: '',
            runtime: '',
            genre: '',
            director: ''
        }
    }

    componentDidMount() {
        this.setState({
            year: this.props.item.Year,
            runtime: this.props.item.Runtime,
            genre: this.props.item.Genre,
            director: this.props.item.Director
        })
    }

    _setOpenModalKey(key) {
        this.props.setOpenModalKey(key)
    }

    _renderButtons() {
        try {
            return (
                <div className="container">
                    {/* Button to Open the Modal  */}
                    <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#update" onClick={() => this._setOpenModalKey(this.props.index)}>
                        update
                    </button>

                    {/* The Modal  */}
                    <div className="modal" id="update">
                        <div className="modal-dialog">
                            <div className="modal-content">

                                {/* Modal Header */}
                                <div className="modal-header">
                                    <h4 className="modal-title">Update Movie Details</h4>
                                    <button type="button" className="close" data-dismiss="modal" onClick={() => this.props.setOpenModalKey(-99)}>&times;</button>
                                </div>

                                {/* Modal body  */}
                                <div className="modal-body">
                                    <div><span>{'Year: '} </span><input type="text" name="year" value={this.state.year} onChange={(e) => this.setState({ 'year': e.target.value })}></input></div>
                                    <div><span>{'Runtime: '}</span><input type="text" name="runtime" value={this.state.runtime} onChange={(e) => this.setState({ 'runtime': e.target.value })}></input></div>
                                    <div><span>{'Genre: '}</span><input type="text" name="genre" value={this.state.genre} onChange={(e) => this.setState({ 'genre': e.target.value })}></input></div>
                                    <div><span>{'Director: '}</span><input type="text" name="director" value={this.state.director} onChange={(e) => this.setState({ 'director': e.target.value })}></input></div>
                                </div>

                                {/* Modal footer  */}
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-danger" data-dismiss="modal" onClick={() => this.props.setOpenModalKey(-99)}>Close</button>
                                </div>

                            </div>
                        </div>
                    </div>

                </div>
            );
        }
        catch (err) { }
    }

    render() {
        return this._renderButtons()
    }
}

export default (Buttons);
