export default (state = {}, action) => {
    state = {
        moviesList: []
    }
    switch (action.type) {
        case 'GET_MOVIES_LIST':
            let moviesList = action.payload;

            return {
                ...state,
                moviesList: moviesList
            }
        default:
            return state
    }
    return state;
}
