import * as actions from '../actions/general.act'

export default store => next => action => {
    if(action.type=='API'){
        const SERVER_ENDPOINT="http://localhost:3000/moviesList/1";
        let {type}=action.payload;

        // store.dispatch(actions.api(type))

        fetch(SERVER_ENDPOINT).then((res)=>{
            console.log('xxxxx',res)
        })
        .catch((err)=>console.log(err))
    }
    return next(action)
}
