import React, { Component } from 'react';
// import './App.css';

class Buttons extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "imdbID": '',
            year: '',
            runtime: '',
            genre: '',
            director: '',
            inputsValidation: { 'year': true, 'runtime': true, 'genre': true, 'director': true },
            isValid: true,
        }
    }

    componentDidMount() {
        this.setState({
            "imdbID": this.props.item.imdbID,
            year: this.props.item.Year,
            runtime: this.props.item.Runtime,
            genre: this.props.item.Genre,
            director: this.props.item.Director,
        })
    }

    _setOpenModalKey(key) {
        this.props.setOpenModalKey(key)
    }

    _update() {
        let newData = this._setDataToUpdate();

        this.props.update(newData);
    }

    _setDataToUpdate() {
        let currentData = this.props.general.moviesList;

        for (var i = 0; i < currentData.length; ++i) {
            let movie = currentData[i];

            if (movie.imdbID === this.state.imdbID) {
                movie.Year = this.state.year;
                movie.Runtime = this.state.runtime;
                movie.Genre = this.state.genre;
                movie.Director = this.state.director;
            }
        }

        return currentData;
    }

    _validation(inputCheck) {
        let validationObj = {
            'year': (val) => {
                if (isNaN(val) || val.length != 4 || val < 0) {
                    return {
                        status: 'error',
                        msg: 'year is not valid'
                    }
                }
                return { status: 'ok' }
            },
            'runtime': (val) => {

            },
            'genre': (val) => {

            },
            'director': (val) => {
                let letters = /^[A-Za-z]+$/;

                if (val.test(letters)) {
                    return { status: 'ok' }
                }
                return {
                    status: 'error',
                    msg: 'input is not valid'
                }
            },
        }
        let InputValidation = validationObj[inputCheck](this.refs[inputCheck].value);
        let { inputsValidation } = this.state;

        if (InputValidation.status == 'error') {
            this.refs[inputCheck].classList.add('invalid');
            inputsValidation[inputCheck] = false;
            this.setState({ isValid: false, inputsValidation });
        }
        else if (InputValidation.status == 'ok') {
            if (this.refs[inputCheck].className.includes('invalid')) {
                this.refs[inputCheck].classList.remove('invalid');
                inputsValidation[inputCheck] = true;

                this.setState({ isValid: true, inputsValidation })
            }
        }
    }

    _renderButtons() {
        try {
            return (
                <div className="container">
                    {/* Button to Open the Modal  */}
                    <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#update" onClick={() => this._setOpenModalKey(this.props.index)}>
                        update
                    </button>

                    {/* The Modal  */}
                    <div className="modal" id="update">
                        <div className="modal-dialog">
                            <div className="modal-content">

                                {/* Modal Header */}
                                <div className="modal-header">
                                    <h4 className="modal-title">Update Movie Details</h4>
                                    <button type="button" className="close" data-dismiss="modal" onClick={() => this.props.setOpenModalKey(-99)}>&times;</button>
                                </div>

                                {/* Modal body  */}
                                <div className="modal-body">
                                    <div><span>{'Year: '} </span><input type="text" name="year" value={this.state.year} ref='year' onChange={(e) => { this._validation('year'); this.setState({ 'year': this.refs.year.value }); }}></input>{!this.state.inputsValidation['year'] && <span className="error-message">Oops! Something went wrong!</span>}</div>
                                    <div><span>{'Runtime: '}</span><input type="text" name="runtime" value={this.state.runtime} onChange={(e) => this.setState({ 'runtime': e.target.value })}></input>{!this.state.inputsValidation['runtime'] && <span className="error-message">Oops! Something went wrong!</span>}</div>
                                    <div><span>{'Genre: '}</span><input type="text" name="genre" value={this.state.genre} onChange={(e) => this.setState({ 'genre': e.target.value })}></input>{!this.state.inputsValidation['genre'] && <span className="error-message">Oops! Something went wrong!</span>}</div>
                                    <div><span>{'Director: '}</span><input type="text" name="director" value={this.state.director} onChange={(e) => this.setState({ 'director': e.target.value })}></input>{!this.state.inputsValidation['director'] && <span className="error-message">Oops! Something went wrong!</span>}</div>
                                </div>

                                {/* Modal footer  */}
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-danger" data-dismiss={this.state.isValid ? "modal" : ''} onClick={() => { if (this.state.isValid) { this.props.setOpenModalKey(-99); this._update() } }}>Update</button>
                                    <button type="button" className="btn btn-danger" data-dismiss="modal" onClick={() => this.props.setOpenModalKey(-99)}>Close</button>
                                </div>

                            </div>
                        </div>
                    </div>

                </div>
            );
        }
        catch (err) { }
    }

    render() {
        return this._renderButtons()
    }
}

export default (Buttons);
