import React, { Component } from 'react';
// import './App.css';

class AddNewMovie extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "imdbID": '',
            title: '',
            year: '',
            runtime: '',
            genre: '',
            director: '',
            inputsValidation: { 'year': true, 'runtime': true, 'genre': true, 'director': true },
            isValid: true,
        }
    }



    _add() {
        let addFormData = {
            title: this.state.title,
            year: this.state.year
        }

        let id = this._generateMovieId();

        this.props.addMovie(addFormData, id);
    }

    _generateMovieId() {
        let id = Math.floor((Math.random() * 1000) + 1);

        while (this._isIdExist(id)) {
            id = Math.floor((Math.random() * 1000) + 1);
        }

        return id;
    }

    _isIdExist(id) {
        let moviesList = this.props.general.moviesList;

        for (var i = 0; i < moviesList.length; ++i) {
            let movie = moviesList[i];

            if (movie.id == id) {
                return true;
            }
        }

        return false;
    }

    _update(index) {
        let newDatas = this._setDataToUpdate();
        let newDataToMerge = {
            id: index + 1,
            year: this.state.year,
            runtime: this.state.runtime,
            genre: this.state.genre,
            director: this.state.director,
        }
        let newData = Object.assign(this.props.item, newDataToMerge);



        this.props.update(newDatas, newData, index + 1);
    }

    _validation(inputCheck) {
        let validationObj = {
            'year': (val) => {
                if (isNaN(val) || val.length != 4 || val < 0) {
                    return {
                        status: 'error',
                        msg: 'year is not valid'
                    }
                }
                return { status: 'ok' }
            },
            'runtime': (val) => {
                if (isNaN(val) || val < 0) {
                    return {
                        status: 'error',
                        msg: 'year is not valid'
                    }
                }
                return { status: 'ok' }
            },
            'genre': (val) => {

            },
            'director': (val) => {
                let letters = /^[A-Za-z]{2,}\s[A-Za-z-']{3,}$/;
                let director = val.split(',');
                let filterDirector = [];

                director.forEach((item, index) => {
                    if (item.charAt(0) == ' ') {
                        director[index] = item.substr(1);
                    }
                });

                filterDirector = director.filter((item, index) => {
                    return !letters.test(item)
                })

                if (filterDirector.length > 0) {
                    return {
                        status: 'error',
                        msg: 'input is not valid'
                    }
                }
                return { status: 'ok' }
            },
        }

        let InputValidation = validationObj[inputCheck](this.refs[inputCheck].value);
        let { inputsValidation } = this.state;

        if (InputValidation.status == 'error') {
            this.refs[inputCheck].classList.add('invalid');
            inputsValidation[inputCheck] = false;
            this.setState({ isValid: false, inputsValidation });
        }
        else if (InputValidation.status == 'ok') {
            if (this.refs[inputCheck].className.includes('invalid')) {
                this.refs[inputCheck].classList.remove('invalid');
                inputsValidation[inputCheck] = true;

                this.setState({ isValid: true, inputsValidation })
            }
        }
    }

    _getRuntime() {
        let runtime = this.state.runtime.split(' ');

        return runtime[0];
    }

    _renderButtons() {
        try {
            return (
                <div className="container">
                    {/* Button to Open the Modal  */}
                    <div className="btnContainer">
                        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#add" onClick={() => { if (this.state.isValid) { this.props.setOpenModalKey(-99); this._add(this.props.index) } }}><span>Add</span></button>
                    </div >

                    {/* The add Modal  */}
                    <div className="modal" id="add">
                        <div className="modal-dialog">
                            <div className="modal-content">

                                {/* Modal Header */}
                                <div className="modal-header">
                                    <h4 className="modal-title">Add New Movie </h4>
                                    <button type="button" className="close" data-dismiss="modal" >&times;</button>
                                </div>

                                {/* Modal body  */}
                                <div className="modal-body">
                                    <div><span>{'Title: '} </span><input type="text" name="title" value={this.state.title} ref='title' onChange={(e) => {  this.setState({ 'title': this.refs.title.value }); }}></input>{!this.state.inputsValidation['year'] && <span className="error-message">Oops! Input invalid!</span>}</div>
                                    <div><span>{'Year: '} </span><input type="text" name="year" value={this.state.year} ref='year' onChange={(e) => { this._validation('year'); this.setState({ 'year': this.refs.year.value }); }}></input>{!this.state.inputsValidation['year'] && <span className="error-message">Oops! Input invalid!</span>}</div>
                                    <div><span>{'Runtime (Minutes): '}</span><input type="text" name="runtime" value={this._getRuntime()} ref='runtime' onChange={(e) => this.setState({ 'runtime': e.target.value })}></input>{!this.state.inputsValidation['runtime'] && <span className="error-message">Oops! Input invalid!</span>}</div>
                                    <div><span>{'Genre: '}</span><input type="text" name="genre" value={this.state.genre} ref='genre' onChange={(e) => this.setState({ 'genre': e.target.value })}></input>{!this.state.inputsValidation['genre'] && <span className="error-message">Oops! Input invalid!</span>}</div>
                                    <div><span>{'Director: '}</span><input type="text" name="director" value={this.state.director} ref='director' onChange={(e) => { this._validation('director'); this.setState({ 'director': e.target.value }) }}></input>{!this.state.inputsValidation['director'] && <span className="error-message">Oops! Input invalid!</span>}</div>
                                </div>

                                {/* Modal footer  */}
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-danger" data-dismiss={this.state.isValid ? "modal" : ''} onClick={() => { if (this.state.isValid) { this.props.setOpenModalKey(-99); this._add(this.props.index) } }}>Add</button>
                                    <button type="button" className="btn btn-danger" data-dismiss="modal" >Close</button>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            );
        }
        catch (err) { }
    }

    render() {
        return this._renderButtons()
    }
}

export default (AddNewMovie);
